import { useEffect, useRef, useState } from 'react';
import './loading.css'

const Loading = ()=>{

    const one = useRef();
    const two = useRef();
    const three = useRef();
    const [count, setCount] = useState(0);

    useEffect(()=>{
        if(count === 0) setCount(count+1);
        else{

            const loadAnimation = ()=>{
                one.current.style.transform = "translateY(-10em)";
                one.current.style.transform = "translateY(1em)";
                two.current.style.transfrom = "translateY(-1em)";
                two.current.style.transfrom = "translateY(1em)";
                three.current.style.transfrom = "translateY(-1em)";
                three.current.style.transfrom = "translateY(1em)";
            }

            while(1){
                loadAnimation();
            }

        }
    },[count])

    return(
        <div className="load">
            <div ref={one}></div>
            <div ref={two}></div>
            <div ref={three}></div>
        </div>
    );
}

export default Loading;
